import { Component, OnInit } from '@angular/core';
import { LoginService } from '../login.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit{

  public loggedIn = false;

 constructor(private loginService:LoginService) {}

  ngOnInit(): void {

this.loggedIn= this.loginService.isLoggedIn();

  }


logoutUser(){
  this.loginService.logout();
}



}
<mat-toolbar color="primary">

<span><a mat-icon-button routerLink="/">Login App</a></span>

<span class="example-spacer">
</span>

<a  *ngIf="!loggedIn" mat-icon-button style="margin-right: 30px;" routerLink="/login">Login</a>

<a *ngIf="loggedIn"  mat-icon-button style="margin-right: 30px;" >User</a>


<a *ngIf="loggedIn" (cl)="logoutUser()"  mat-icon-button style="margin-right: 30px;" >Logout</a>



</mat-toolbar>
